package ch03;

public class BitOperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		System.out.println("num = " + num);
		System.out.println("1의 보수 값 = "+(~num));
		//비트 전환 연산자
		System.out.println("2의 보수 음수 값 = "+(~num + 1));
	}

}
